/**
 * Returns a ready-to-render SVG string for the given technology name.
 *
 * Falls back to a generic code-brackets icon when the name is unknown.
 */
export function getTechIconSvg(name: string, size: number): string {
	const factory = TECH_ICONS[name];
	return factory ? factory(size) : FALLBACK_ICON(size);
}
